script:
    // Theme initialization
    document.addEventListener('DOMContentLoaded', function() {
      // Initialize theme functionality
      initializeTheme();
    });

    function initializeTheme() {
      initializeDarkMode();
      initializeMobileMenu();
      initializeBackToTop();
      initializeCookieConsent();
      initializeSocialSharing();
      initializeTableOfContents();
      initializeRelatedPosts();
      initializeLazyLoading();
      initializeReadingProgress();
      initializeAds();
    }

    // Dark mode functionality
    function initializeDarkMode() {
      const darkModeToggle = document.getElementById('dark-mode-toggle');
      const body = document.body;

      // Check for saved dark mode preference
      const isDarkMode = localStorage.getItem('darkMode') === 'true';
      if (isDarkMode) {
        body.classList.add('dark-mode');
        darkModeToggle.setAttribute('aria-pressed', 'true');
        darkModeToggle.textContent = 'â˜€ï¸';
      }

      darkModeToggle.addEventListener('click', function() {
        body.classList.toggle('dark-mode');
        const isDark = body.classList.contains('dark-mode');
        localStorage.setItem('darkMode', isDark);
        darkModeToggle.setAttribute('aria-pressed', isDark);
        darkModeToggle.textContent = isDark ? 'â˜€ï¸' : 'ðŸŒ™';
      });
    }

    // Mobile menu functionality
    function initializeMobileMenu() {
      const mobileMenuToggle = document.getElementById('mobile-menu-toggle');
      const mainNav = document.getElementById('main-nav');

      mobileMenuToggle.addEventListener('click', function() {
        mainNav.classList.toggle('open');
        const isOpen = mainNav.classList.contains('open');
        mobileMenuToggle.setAttribute('aria-expanded', isOpen);
        mobileMenuToggle.textContent = isOpen ? 'âœ•' : 'â˜°';
      });
    }

    // Back to top functionality
    function initializeBackToTop() {
      const backToTopButton = document.getElementById('back-to-top');

      window.addEventListener('scroll', function() {
        if (window.pageYOffset > 300) {
          backToTopButton.style.display = 'block';
        } else {
          backToTopButton.style.display = 'none';
        }
      });

      backToTopButton.addEventListener('click', function() {
        window.scrollTo({ top: 0, behavior: 'smooth' });
      });
    }

    // Cookie consent functionality
    function initializeCookieConsent() {
      const cookieBanner = document.getElementById('cookie-consent-banner');
      const acceptButton = document.getElementById('accept-cookies');

      if (!localStorage.getItem('cookieConsent')) {
        cookieBanner.classList.add('show');
      }

      acceptButton.addEventListener('click', function() {
        localStorage.setItem('cookieConsent', 'true');
        cookieBanner.classList.remove('show');
      });
    }

    // Social sharing functionality
    function initializeSocialSharing() {
      const shareButtons = document.querySelectorAll('.share-btn, .sidebar-share');

      shareButtons.forEach(button => {
        button.addEventListener('click', function(e) {
          e.preventDefault();
          const platform = this.dataset.platform;
          const url = encodeURIComponent(window.location.href);
          const title = encodeURIComponent(document.title);

          let shareUrl = '';
          switch (platform) {
            case 'facebook':
              shareUrl = `https://www.facebook.com/sharer/sharer.php?u=${url}`;
              break;
            case 'twitter':
              shareUrl = `https://twitter.com/intent/tweet?text=${title}&url=${url}`;
              break;
            case 'linkedin':
              shareUrl = `https://www.linkedin.com/sharing/share-offsite/?url=${url}`;
              break;
            case 'whatsapp':
              shareUrl = `https://wa.me/?text=${title} ${url}`;
              break;
            case 'telegram':
              shareUrl = `https://t.me/share/url?url=${url}&text=${title}`;
              break;
            case 'email':
              shareUrl = `mailto:?subject=${title}&body=${url}`;
              break;
          }

          if (shareUrl) {
            window.open(shareUrl, '_blank', 'width=600,height=400');
          }
        });
      });
    }

    // Table of contents functionality
    function initializeTableOfContents() {
      if (document.body.classList.contains('page-type-item')) {
        generateTableOfContents();
      }
    }

    function generateTableOfContents() {
      const postContent = document.querySelector('.post-content');
      const tocContainer = document.getElementById('toc-container');
      const tocList = document.getElementById('toc-list');

      if (!postContent || !tocContainer || !tocList) return;

      const headings = postContent.querySelectorAll('h1, h2, h3, h4, h5, h6');

      if (headings.length > 2) {
        tocContainer.style.display = 'block';

        headings.forEach((heading, index) => {
          const id = `toc-${index}`;
          heading.id = id;

          const listItem = document.createElement('li');
          listItem.className = `toc-${heading.tagName.toLowerCase()}`;

          const link = document.createElement('a');
          link.href = `#${id}`;
          link.textContent = heading.textContent;
          link.addEventListener('click', function(e) {
            e.preventDefault();
            heading.scrollIntoView({ behavior: 'smooth' });
          });

          listItem.appendChild(link);
          tocList.appendChild(listItem);
        });

        // Insert TOC after first paragraph
        const firstParagraph = postContent.querySelector('p');
        if (firstParagraph) {
          firstParagraph.after(tocContainer);
        }
      }
    }

    // Related posts functionality
    function initializeRelatedPosts() {
      if (document.body.classList.contains('page-type-item')) {
        loadRelatedPosts();
      }
    }

    function loadRelatedPosts() {
      const relatedContainer = document.getElementById('related-posts');
      if (!relatedContainer) return;

      // Simulate loading related posts
      setTimeout(() => {
        relatedContainer.innerHTML = `





Related Post 1

This is a related post snippet...




Related Post 2

This is another related post snippet...




Related Post 3

This is yet another related post snippet...


        `;
        relatedContainer.classList.remove('loading-placeholder');
      }, 1000);
    }

    // Lazy loading functionality
    function initializeLazyLoading() {
      const images = document.querySelectorAll('img[data-src]');

      const imageObserver = new IntersectionObserver((entries, observer) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            const img = entry.target;
            img.src = img.dataset.src;
            img.classList.remove('lazy');
            observer.unobserve(img);
          }
        });
      });

      images.forEach(img => imageObserver.observe(img));
    }

    // Reading progress functionality
    function initializeReadingProgress() {
      if (document.body.classList.contains('page-type-item')) {
        const progressBar = document.createElement('div');
        progressBar.id = 'reading-progress';
        progressBar.style.cssText = `
          position: fixed;
          top: 0;
          left: 0;
          width: 0%;
          height: 3px;
          background: #007bff;
          z-index: 1000;
          transition: width 0.3s ease;
        `;
        document.body.appendChild(progressBar);

        window.addEventListener('scroll', function() {
          const scrollTop = window.pageYOffset;
          const docHeight = document.body.scrollHeight - window.innerHeight;
          const scrollPercent = (scroll
