body.script:
    // Theme initialization
    document.addEventListener('DOMContentLoaded', function() {
      // Initialize theme functionality
      initializeTheme();
    });
    
    function initializeTheme() {
      initializeDarkMode();
      initializeMobileMenu();
      initializeBackToTop();
      initializeCookieConsent();
      initializeSocialSharing();
      initializeTableOfContents();
      initializeRelatedPosts();
      initializeLazyLoading();
      initializeReadingProgress();
      initializeAds();
    }
    
    // Dark mode functionality
    function initializeDarkMode() {
      const darkModeToggle = document.getElementById('dark-mode-toggle');
      const body = document.body;
      
      // Check for saved dark mode preference
      const isDarkMode = localStorage.getItem('darkMode') === 'true';
      if (isDarkMode) {
        body.classList.add('dark-mode');
        darkModeToggle.setAttribute('aria-pressed', 'true');
        darkModeToggle.textContent = 'â˜€ï¸';
      }
      
      darkModeToggle.addEventListener('click', function() {
        body.classList.toggle('dark-mode');
        const isDark = body.classList.contains('dark-mode');
        localStorage.setItem('darkMode', isDark);
        darkModeToggle.setAttribute('aria-pressed', isDark);
        darkModeToggle.textContent = isDark ? 'â˜€ï¸' : 'ðŸŒ™';
      });
    }
    
    // Mobile menu functionality
    function initializeMobileMenu() {
      const mobileMenuToggle = document.getElementById('mobile-menu-toggle');
      const mainNav = document.getElementById('main-nav');
      
      mobileMenuToggle.addEventListener('click', function() {
        mainNav.classList.toggle('open');
        const isOpen = mainNav.classList.contains('open');
        mobileMenuToggle.setAttribute('aria-expanded', isOpen);
        mobileMenuToggle.textContent = isOpen ? 'âœ•' : 'â˜°';
      });
    }
    
    // Back to top functionality
    function initializeBackToTop() {
      const backToTopButton = document.getElementById('back-to-top');
      
      window.addEventListener('scroll', function() {
        if (window.pageYOffset > 300) {
          backToTopButton.style.display = 'block';
        } else {
          backToTopButton.style.display = 'none';
        }
      });
      
      backToTopButton.addEventListener('click', function() {
        window.scrollTo({ top: 0, behavior: 'smooth' });
      });
    }
    
    // Cookie consent functionality
    function initializeCookieConsent() {
      const cookieBanner = document.getElementById('cookie-consent-banner');
      const acceptButton = document.getElementById('accept-cookies');
      
      if (!localStorage.getItem('cookieConsent')) {
        cookieBanner.classList.add('show');
      }
      
      acceptButton.addEventListener('click', function() {
        localStorage.setItem('cookieConsent', 'true');
        cookieBanner.classList.remove('show');
      });
    }
    
    // Social sharing functionality
    function initializeSocialSharing() {
      const shareButtons = document.querySelectorAll('.share-btn, .sidebar-share');
      
      shareButtons.forEach(button => {
        button.addEventListener('click', function(e) {
          e.preventDefault();
          const platform = this.dataset.platform;
          const url = encodeURIComponent(window.location.href);
          const title = encodeURIComponent(document.title);
          
          let shareUrl = '';
          switch (platform) {
            case 'facebook':
              shareUrl = `https://www.facebook.com/sharer/sharer.php?u=${url}`;
              break;
            case 'twitter':
              shareUrl = `https://twitter.com/intent/tweet?text=${title}&url=${url}`;
              break;
            case 'linkedin':
              shareUrl = `https://www.linkedin.com/sharing/share-offsite/?url=${url}`;
              break;
            case 'whatsapp':
              shareUrl = `https://wa.me/?text=${title} ${url}`;
              break;
            case 'telegram':
              shareUrl = `https://t.me/share/url?url=${url}&text=${title}`;
              break;
            case 'email':
              shareUrl = `mailto:?subject=${title}&body=${url}`;
              break;
          }
          
          if (shareUrl) {
            window.open(shareUrl, '_blank', 'width=600,height=400');
          }
        });
      });
    }
    
    // Table of contents functionality
    function initializeTableOfContents() {
      if (document.body.classList.contains('page-type-item')) {
        generateTableOfContents();
      }
    }
    
    function generateTableOfContents() {
      const postContent = document.querySelector('.post-content');
      const tocContainer = document.getElementById('toc-container');
      const tocList = document.getElementById('toc-list');
      
      if (!postContent || !tocContainer || !tocList) return;
      
      const headings = postContent.querySelectorAll('h1, h2, h3, h4, h5, h6');
      
      if (headings.length > 2) {
        tocContainer.style.display = 'block';
        
        headings.forEach((heading, index) => {
          const id = `toc-${index}`;
          heading.id = id;
          
          const listItem = document.createElement('li');
          listItem.className = `toc-${heading.tagName.toLowerCase()}`;
          
          const link = document.createElement('a');
          link.href = `#${id}`;
          link.textContent = heading.textContent;
          link.addEventListener('click', function(e) {
            e.preventDefault();
            heading.scrollIntoView({ behavior: 'smooth' });
          });
          
          listItem.appendChild(link);
          tocList.appendChild(listItem);
        });
        
        // Insert TOC after first paragraph
        const firstParagraph = postContent.querySelector('p');
        if (firstParagraph) {
          firstParagraph.after(tocContainer);
        }
      }
    }
    
    // Related posts functionality
    function initializeRelatedPosts() {
      if (document.body.classList.contains('page-type-item')) {
        loadRelatedPosts();
      }
    }
    
    function loadRelatedPosts() {
      const relatedContainer = document.getElementById('related-posts');
      if (!relatedContainer) return;
      
      // Simulate loading related posts
      setTimeout(() => {
        relatedContainer.innerHTML = `





Related Post 1

This is a related post snippet...
            



Related Post 2

This is another related post snippet...
            



Related Post 3

This is yet another related post snippet...
            
          
        `;
        relatedContainer.classList.remove('loading-placeholder');
      }, 1000);
    }
    
    // Lazy loading functionality
    function initializeLazyLoading() {
      const images = document.querySelectorAll('img[data-src]');
      
      const imageObserver = new IntersectionObserver((entries, observer) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            const img = entry.target;
            img.src = img.dataset.src;
            img.classList.remove('lazy');
            observer.unobserve(img);
          }
        });
      });
      
      images.forEach(img => imageObserver.observe(img));
    }
    
    // Reading progress functionality
    function initializeReadingProgress() {
      if (document.body.classList.contains('page-type-item')) {
        const progressBar = document.createElement('div');
        progressBar.id = 'reading-progress';
        progressBar.style.cssText = `
          position: fixed;
          top: 0;
          left: 0;
          width: 0%;
          height: 3px;
          background: #007bff;
          z-index: 1000;
          transition: width 0.3s ease;
        `;
        document.body.appendChild(progressBar);
        
       window.addEventListener('scroll', function() {
          const scrollTop = window.pageYOffset;
          const docHeight = document.body.scrollHeight - window.innerHeight;
          const scrollPercent = (scrollTop / docHeight) * 100;
          progressBar.style.width = scrollPercent + '%';
        });
      }
    }
    
    // Advertisement functionality
    function initializeAds() {
      const adContainers = document.querySelectorAll('.ad-container');
      
      adContainers.forEach(container => {
        // Add loading state
        container.classList.add('ad-loading');
        
        // Simulate ad loading
        setTimeout(() => {
          container.classList.remove('ad-loading');
          container.classList.add('ad-loaded');
        }, 1000);
      });
    }
    
    // Search functionality
    function initializeSearch() {
      const searchForm = document.querySelector('.search-form');
      const searchInput = document.querySelector('.search-input');
      
      if (searchForm && searchInput) {
        searchForm.addEventListener('submit', function(e) {
          if (searchInput.value.trim() === '') {
            e.preventDefault();
            searchInput.focus();
          }
        });
        
        // Search suggestions (basic implementation)
        searchInput.addEventListener('input', function() {
          const query = this.value.toLowerCase();
          if (query.length > 2) {
            // Implement search suggestions here
            console.log('Search query:', query);
          }
        });
      }
    }
    
    // Newsletter subscription
    function initializeNewsletter() {
      const newsletterForm = document.querySelector('.newsletter-form');
      const emailInput = document.querySelector('.newsletter-email');
      
      if (newsletterForm && emailInput) {
        newsletterForm.addEventListener('submit', function(e) {
          e.preventDefault();
          const email = emailInput.value.trim();
          
          if (email && isValidEmail(email)) {
            // Implement newsletter subscription
            alert('Thank you for subscribing!');
            emailInput.value = '';
          } else {
            alert('Please enter a valid email address.');
          }
        });
      }
    }
    
    function isValidEmail(email) {
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      return emailRegex.test(email);
    }
    
    // Smooth scrolling for anchor links
    function initializeSmoothScrolling() {
      const anchorLinks = document.querySelectorAll('a[href^="#"]');
      
      anchorLinks.forEach(link => {
        link.addEventListener('click', function(e) {
          e.preventDefault();
          const targetId = this.getAttribute('href').substring(1);
          const targetElement = document.getElementById(targetId);
          
          if (targetElement) {
            targetElement.scrollIntoView({ behavior: 'smooth' });
          }
        });
      });
    }
    
    // Initialize additional features
    document.addEventListener('DOMContentLoaded', function() {
      initializeSearch();
      initializeNewsletter();
      initializeSmoothScrolling();
    });
    
    // Performance optimization
    function debounce(func, delay) {
      let timeoutId;
      return function (...args) {
        clearTimeout(timeoutId);
        timeoutId = setTimeout(() => func.apply(this, args), delay);
      };
    }
    
    // Optimize scroll events
    window.addEventListener('scroll', debounce(function() {
      // Scroll-based functionality
    }, 100));
    
    // Error handling
    window.addEventListener('error', function(event) {
      console.error('Theme error:', event.error);
    });
    
    // Performance monitoring
    window.addEventListener('load', function() {
      // Log performance metrics
      if (window.performance && window.performance.timing) {
        const timing = window.performance.timing;
        const loadTime = timing.loadEventEnd - timing.navigationStart;
        console.log('Page load time:', loadTime + 'ms');
      }
    });
    
    // Service worker registration (optional)
    if ('serviceWorker' in navigator) {
      window.addEventListener('load', function() {
        navigator.serviceWorker.register('/sw.js').then(function(registration) {
          console.log('ServiceWorker registration successful');
        }).catch(function(err) {
          console.log('ServiceWorker registration failed: ', err);
        });
      });
    }
    
    // Google Analytics integration
    function initializeAnalytics() {
      // Google Analytics tracking code
      if (typeof gtag !== 'undefined') {
        gtag('config', 'GA_TRACKING_ID');
      }
    }
    
    // Social media integration
    function initializeSocialFeatures() {
      // Facebook SDK
      if (window.FB) {
        window.FB.init({
          appId: 'YOUR_APP_ID',
          cookie: true,
          xfbml: true,
          version: 'v18.0'
        });
      }
      
      // Twitter widgets
      if (window.twttr) {
        window.twttr.widgets.load();
      }
    }
    
    // Accessibility improvements
    function initializeAccessibility() {
      // Skip to content link
      const skipLink = document.createElement('a');
      skipLink.href = '#main-content';
      skipLink.textContent = 'Skip to content';
      skipLink.className = 'skip-link';
      document.body.insertBefore(skipLink, document.body.firstChild);
      
      // Keyboard navigation
      document.addEventListener('keydown', function(e) {
        if (e.key === 'Tab') {
          document.body.classList.add('keyboard-navigation');
        }
      });
      
      document.addEventListener('mousedown', function() {
        document.body.classList.remove('keyboard-navigation');
      });
    }
    
    // Initialize all features
    document.addEventListener('DOMContentLoaded', function() {
      initializeAnalytics();
      initializeSocialFeatures();
      initializeAccessibility();
    });
    
    // Theme customization API
    window.ForgeDaily = {
      version: '1.0.0',
      
      // Custom methods for theme customization
      addWidget: function(widget) {
        // Add custom widget functionality
      },
      
      setThemeColor: function(color) {
        document.documentElement.style.setProperty('--primary-color', color);
      },
      
      toggleSidebar: function() {
        const sidebar = document.querySelector('.sidebar');
        sidebar.classList.toggle('hidden');
      },
      
      refreshContent: function() {
        // Refresh dynamic content
      }
    };

window.dataLayer = window.dataLayer || [];
function gtag(){dataLayer.push(arguments);}
gtag('js', new Date());
gtag('config', 'GA_TRACKING_ID');
